dmgEN.GETpROPERTY ("APP.PACKAGE.DMGEN.MANAGER").REPLACE ('.', SEPARATOR.CHARaT(0));
        dmgEN.SETpROPERTY ("DMGEN.MANAGER.PATH", PROP);
        IF (dmgEN.GETpROPERTY ("APP.PATTERN").SEARCH ("APP.EXE.STANDARD") >= 0)
         {
          dmgEN.SETpROPERTY ("DMGEN.PATTERN.EXE", "TRUE");
