       VAR NEXT = TRUE;
       DRIVERvALUE = dmgEN.GETpROPERTY ("DB.DRIVER");
       LOGINvALUE =  dmgEN.GETpROPERTY ("DB.LOGIN");
       PASSWORDvALUE = dmgEN.GETpROPERTY ("DB.PASSWORD");
       URLvALUE = dmgEN.GETpROPERTY ("DB.URL");
       IF (URLvALUE != NULL)
         {
          DRIVER = THEpROPfILE.CREATEeNTRY ();
          DRIVER.SETkEY ("DRIVER.0");
          DRIVER.SETvALUE (DRIVERvALUE);
          LOGIN = THEpROPfILE.CREATEeNTRY ();
          LOGIN.SETkEY ("LOGIN.0");
          LOGIN.SETvALUE (LOGINvALUE);
          PASSWORD = THEpROPfILE.CREATEeNTRY ();
          PASSWORD.SETkEY ("PASSWORD.0");
          PASSWORD.SETvALUE (PASSWORDvALUE);
          SERVER = THEpROPfILE.CREATEeNTRY ();
          SERVER.SETkEY ("SERVER.0");
          SERVER.SETvALUE (URLvALUE);
         }
       FOR (I = 1; NEXT; I++)
         {
          TRY
            {
             DRIVERvALUE = dmgEN.GETpROPERTY ("DB.DRIVER." + I);
             LOGINvALUE =  dmgEN.GETpROPERTY ("DB.LOGIN." + I);
             PASSWORDvALUE = dmgEN.GETpROPERTY ("DB.PASSWORD." + I);
             URLvALUE = dmgEN.GETpROPERTY ("DB.URL." + I);
             IF (URLvALUE != NULL)
               {
                SERVER = THEpROPfILE.CREATEeNTRY ();
                SERVER.SETkEY ("SERVER." + I);
                SERVER.SETvALUE (URLvALUE);
                IF (DRIVERvALUE != NULL)
                  {
                   DRIVER = THEpROPfILE.CREATEeNTRY ();
                   DRIVER.SETkEY ("DRIVER." + I);
                   DRIVER.SETvALUE (DRIVERvALUE);
                  }
                IF (LOGINvALUE != NULL)
                  {
                   LOGIN = THEpROPfILE.CREATEeNTRY ();
                   LOGIN.SETkEY ("LOGIN." + I);
                   LOGIN.SETvALUE (LOGINvALUE);
                  }
                IF (PASSWORDvALUE != NULL)
                  {
                   PASSWORD = THEpROPfILE.CREATEeNTRY ();
                   PASSWORD.SETkEY ("PASSWORD." + I);
                   PASSWORD.SETvALUE (PASSWORDvALUE);
                  }
               }
             ELSE
               NEXT = FALSE;
            }
          CATCH (qQQQ)
            {
             NEXT = FALSE;
            }
         }
       CREATEpROPERTIES.EXECUTE ();


















