       VAR NEXT = TRUE;
       THEeCHO.SETmESSAGE ('<?XML VERSION="1.0" ENCODING="utf-8"?>\N<DATABASES>');
       CREATEdBXML.EXECUTE ();
       DBuRL = dmgEN.GETpROPERTY ("DB.URL");
       DBcACHE = dmgEN.GETpROPERTY ("DB.CACHE.SIZE");
       IF (DBuRL != NULL && DBcACHE != NULL)
         {
          THEeCHO.SETmESSAGE ('\N  <DATABASE ID="0" CACHE-SIZE="' + DBcACHE +
'"/>');
          CREATEdBXML.EXECUTE ();
         }
       FOR (I = 1; NEXT; I++)
        {
         TRY
           {
            DBuRL = dmgEN.GETpROPERTY ("DB.URL." + I);
            DBcACHE = dmgEN.GETpROPERTY ("DB.CACHE.SIZE." + I);
            IF (DBuRL != NULL && DBcACHE != NULL)
              {
               THEeCHO.SETmESSAGE ('\N  <DATABASE ID="' + I +'" CACHE-SIZE="' +
DBcACHE + '"/>');
               CREATEdBXML.EXECUTE ();
              }
            ELSE
              NEXT = FALSE;
           }
         CATCH (qQQQ)
           {
            NEXT = FALSE;
           }
        }
       THEeCHO.SETmESSAGE ('\N</DATABASES>');
       CREATEdBXML.EXECUTE ();




